import torch
import numpy as np
from tqdm import tqdm
from scipy import linalg
from torch.utils.data import DataLoader

def compute_fid(real, fake, ae_model, batch_size=64):

    # Get bottleneck activations from images in dataset
    real_activations = get_activations(real, ae_model, batch_size)
    real_mu = np.mean(real_activations, axis=0)
    real_sigma = np.cov(real_activations, rowvar=False)

    # Get bottleneck activations from images generated by the model
    fake_activations = get_activations(fake, ae_model, batch_size)
    fake_mu = np.mean(fake_activations, axis=0)
    fake_sigma = np.cov(fake_activations, rowvar=False)

    # Compute FID score
    return _fid(real_mu, real_sigma, fake_mu, fake_sigma)


def get_activations(images, ae_model, batch_size=64):

    # Gets the bottlenecks of the AE for given images
    loader = DataLoader(images, batch_size=batch_size)
    preds = []
    with torch.no_grad():
        for batch in tqdm(loader, desc="Computing AE Bottleneck activations"):
            pred = ae_model.encode(batch)
            preds.append(pred)
        preds = torch.cat(preds, dim=0).squeeze().cpu().detach().numpy()
    return preds


def _fid(mu1, sigma1, mu2, sigma2, eps=1e-6):
    """Numpy implementation of the Frechet Distance.
    The Frechet distance between two multivariate Gaussians X_1 ~ N(mu_1, C_1)
    and X_2 ~ N(mu_2, C_2) is
            d^2 = ||mu_1 - mu_2||^2 + Tr(C_1 + C_2 - 2*sqrt(C_1*C_2)).
    Stable version by Dougal J. Sutherland.
    Params:
    -- mu1   : Numpy array containing the activations of a layer of the
               inception net (like returned by the function 'get_predictions')
               for generated samples.
    -- mu2   : The sample mean over activations, precalculated on an
               representive data set.
    -- sigma1: The covariance matrix over activations for generated samples.
    -- sigma2: The covariance matrix over activations, precalculated on an
               representive data set.
    Returns:
    --   : The Frechet Distance.
    """
    mu1 = np.atleast_1d(mu1)
    mu2 = np.atleast_1d(mu2)

    sigma1 = np.atleast_2d(sigma1)
    sigma2 = np.atleast_2d(sigma2)

    assert mu1.shape == mu2.shape, \
        'Training and test mean vectors have different lengths'
    assert sigma1.shape == sigma2.shape, \
        'Training and test covariances have different dimensions'

    diff = mu1 - mu2

    # Product might be almost singular
    covmean, _ = linalg.sqrtm(sigma1.dot(sigma2), disp=False)
    if not np.isfinite(covmean).all():
        msg = ('fid calculation produces singular product; '
               'adding %s to diagonal of cov estimates') % eps
        print(msg)
        offset = np.eye(sigma1.shape[0]) * eps
        covmean = linalg.sqrtm((sigma1 + offset).dot(sigma2 + offset))

    # Numerical error might give slight imaginary component
    if np.iscomplexobj(covmean):
        if not np.allclose(np.diagonal(covmean).imag, 0, atol=1e-3):
            m = np.max(np.abs(covmean.imag))
            raise ValueError('Imaginary component {}'.format(m))
        covmean = covmean.real

    tr_covmean = np.trace(covmean)

    return (diff.dot(diff) + np.trace(sigma1) + np.trace(sigma2) - 2 * tr_covmean)
